    private void ConvertActionPerformed(java.awt.event.ActionEvent evt) {// GEN-FIRST:event_ConvertActionPerformed
        if ((jTextFieldLeft.getText().equals("")) || (jTextFieldRight.getText().equals(""))) {
            JOptionPane.showMessageDialog(null, "You must enter value", " in DST System",
                    JOptionPane.INFORMATION_MESSAGE);
        } else if (jComboBox4.getSelectedItem().equals("Distance")) {
            label1.setText("( Speed )");
            label2.setText("( Time )");
            double dst1 = Double.parseDouble(jTextFieldLeft.getText());
            double dst2 = Double.parseDouble(jTextFieldRight.getText());
            double dst3;
            // distance = speed * time
            jTextFieldL.setText("Distance  =");
            jTextFieldC.setText("Speed  X");
            jTextFieldR.setText("Time");
            dst3 = dst1 * dst2;
            String value = String.format("%.2f", dst3);
            jTextFieldDown.setText(value);
        } else if (jComboBox4.getSelectedItem().equals("Speed")) {
            label1.setText("( Distance )");
            label2.setText("( Time )");
            double dst1 = Double.parseDouble(jTextFieldLeft.getText());
            double dst2 = Double.parseDouble(jTextFieldRight.getText());
            double dst3;
            // speed = distance / time
            jTextFieldL.setText("Speed  =");
            jTextFieldC.setText("Distance  /");
            jTextFieldR.setText("Time");
            dst3 = dst1 / dst2;
            String value = String.format("%.2f", dst3);
            jTextFieldDown.setText(value);
        } else if (jComboBox4.getSelectedItem().equals("Time")) {
            label1.setText("( Distance )");
            label2.setText("( Speed )");
            double dst1 = Double.parseDouble(jTextFieldLeft.getText());
            double dst2 = Double.parseDouble(jTextFieldRight.getText());
            double dst3;
            // time = distance / speed
            jTextFieldL.setText("Time  =");
            jTextFieldC.setText("Distance  /");
            jTextFieldR.setText("Speed");
            dst3 = dst1 / dst2;
            String value = String.format("%.2f", dst3);
            jTextFieldDown.setText(value);
        }
    }// GEN-LAST:event_ConvertActionPerformed

